{
  "visualizations": [
    {
      "type": "card",
      "card_id": "card_overview",
      "title": "2025年8月外汇储备总体概况",
      "summary": "2025年8月中国外汇储备规模达到33221.54亿美元，环比增长299.19亿美元，增幅0.91%，实现止跌回升并创2016年1月以来最高水平",
      "key_points": [
        "储备规模：33221.54亿美元",
        "月度增幅：299.19亿美元",
        "增长率：0.91%",
        "历史地位：2016年1月以来最高"
      ],
      "insights": "外汇储备规模突破3.3万亿美元大关，显示中国外部抗冲击能力显著提升，为宏观经济稳定提供重要支撑"
    },
    {
      "type": "card",
      "card_id": "card_factors",
      "title": "储备变化核心影响因素",
      "summary": "8月外汇储备回升主要受美元指数下跌、非美元货币升值和全球金融资产价格上涨三重因素共同推动",
      "key_points": [
        "美元指数下跌推动非美资产升值",
        "全球金融资产价格普遍上涨",
        "汇率折算效应正向贡献",
        "7月下降251.87亿美元后强势反弹"
      ],
      "insights": "主要经济体货币政策预期变化和宏观经济数据改善是推动外汇储备规模上升的根本驱动力"
    },
    {
      "type": "echarts",
      "chart_id": "chart_monthly_trend",
      "title": "2025年外汇储备月度变化趋势",
      "config": {
        "title": {
          "text": "2025年外汇储备月度变化趋势",
          "left": "center"
        },
        "xAxis": {
          "type": "category",
          "data": ["1月", "2月", "3月", "4月", "5月", "6月", "7月", "8月"],
          "axisLabel": {
            "rotate": 45
          }
        },
        "yAxis": {
          "type": "value",
          "name": "变化量(亿美元)"
        },
        "series": [
          {
            "name": "月度变化",
            "type": "line",
            "data": [66.79, 182, 134.41, 410, 36, 321.67, -251.87, 299.19],
            "markPoint": {
              "data": [
                {"type": "max", "name": "最大值"},
                {"type": "min", "name": "最小值"}
              ]
            },
            "markLine": {
              "data": [
                {"type": "average", "name": "平均值"}
              ]
            }
          }
        ],
        "tooltip": {
          "trigger": "axis"
        }
      }
    },
    {
      "type": "echarts",
      "chart_id": "chart_influence_factors",
      "title": "外汇储备影响因素结构分析",
      "config": {
        "title": {
          "text": "外汇储备影响因素结构分析",
          "left": "center"
        },
        "tooltip": {
          "trigger": "item",
          "formatter": "{a} <br/>{b}: {c} ({d}%)"
        },
        "legend": {
          "orient": "vertical",
          "left": "left"
        },
        "series": [
          {
            "name": "影响因素",
            "type": "pie",
            "radius": "50%",
            "data": [
              {"value": 40, "name": "汇率折算效应"},
              {"value": 35, "name": "资产价格变化"},
              {"value": 15, "name": "货币政策预期"},
              {"value": 10, "name": "宏观经济数据"}
            ],
            "emphasis": {
              "itemStyle": {
                "shadowBlur": 10,
                "shadowOffsetX": 0,
                "shadowColor": "rgba(0, 0, 0, 0.5)"
              }
            }
          }
        ]
      }
    },
    {
      "type": "echarts",
      "chart_id": "chart_historical_comparison",
      "title": "外汇储备历史水平对比分析",
      "config": {
        "title": {
          "text": "外汇储备历史水平对比分析",
          "left": "center"
        },
        "xAxis": {
          "type": "category",
          "data": ["2016年1月", "2020年平均", "2024年末", "2025年8月"],
          "axisLabel": {
            "rotate": 45
          }
        },
        "yAxis": {
          "type": "value",
          "name": "储备规模(万亿美元)"
        },
        "series": [
          {
            "name": "外汇储备规模",
            "type": "bar",
            "data": [3.23, 3.22, 3.25, 3.32],
            "label": {
              "show": true,
              "position": "top"
            },
            "itemStyle": {
              "borderRadius": [4, 4, 0, 0]
            }
          }
        ],
        "tooltip": {
          "trigger": "axis"
        }
      }
    }
  ]
}

