{
  "visualizations": [
    {
      "type": "card",
      "card_id": "card_overview",
      "title": "中国外汇储备总体概况",
      "summary": "截至2025年8月末，中国外汇储备规模达到33221.54亿美元，重新站上3.3万亿美元大关，较7月末增加299.19亿美元，升幅0.91%",
      "key_points": [
        "外汇储备规模：33221.54亿美元",
        "8月环比增长：299.19亿美元",
        "月度增幅：0.91%",
        "储备水平：重新站上3.3万亿美元"
      ],
      "insights": "中国外汇储备在经历7月下降后强势反弹，显示外汇市场稳定性和管理有效性"
    },
    {
      "type": "card",
      "card_id": "card_performance",
      "title": "关键表现指标分析",
      "summary": "2025年1-8月外汇储备呈现波动增长态势，其中4月增幅最大达410亿美元，7月出现唯一负增长，8月实现强劲回升",
      "key_points": [
        "最大单月增幅：4月增加410亿美元",
        "唯一负增长：7月减少251.87亿美元",
        "累计净增长：约1200亿美元",
        "波动特征：呈现明显的月度波动性"
      ],
      "insights": "外汇储备增长呈现明显的季节性波动，4月和6月为增长高峰期，7月为调整期"
    },
    {
      "type": "echarts",
      "chart_id": "chart_trend",
      "title": "2025年外汇储备月度变化趋势",
      "config": {
        "title": {
          "text": "2025年外汇储备月度变化趋势",
          "left": "center"
        },
        "xAxis": {
          "type": "category",
          "data": ["1月", "2月", "3月", "4月", "5月", "6月", "7月", "8月"]
        },
        "yAxis": {
          "type": "value",
          "name": "变化金额（亿美元）"
        },
        "series": [
          {
            "name": "月度变化",
            "type": "line",
            "data": [66.79, 182, 134.41, 410, 36, 321.67, -251.87, 299.19],
            "smooth": true,
            "markPoint": {
              "data": [
                {"type": "max", "name": "最大值"},
                {"type": "min", "name": "最小值"}
              ]
            }
          }
        ],
        "tooltip": {
          "trigger": "axis"
        }
      }
    },
    {
      "type": "echarts",
      "chart_id": "chart_comparison",
      "title": "月度增长贡献度分析",
      "config": {
        "title": {
          "text": "月度增长贡献度分析",
          "left": "center"
        },
        "tooltip": {
          "trigger": "item",
          "formatter": "{a} <br/>{b}: {c}亿美元 ({d}%)"
        },
        "legend": {
          "orient": "vertical",
          "left": "left"
        },
        "series": [
          {
            "name": "增长贡献",
            "type": "pie",
            "radius": "50%",
            "data": [
              {"value": 66.79, "name": "1月"},
              {"value": 182, "name": "2月"},
              {"value": 134.41, "name": "3月"},
              {"value": 410, "name": "4月"},
              {"value": 36, "name": "5月"},
              {"value": 321.67, "name": "6月"},
              {"value": 299.19, "name": "8月"}
            ],
            "emphasis": {
              "itemStyle": {
                "shadowBlur": 10,
                "shadowOffsetX": 0,
                "shadowColor": "rgba(0, 0, 0, 0.5)"
              }
            }
          }
        ]
      }
    },
    {
      "type": "echarts",
      "chart_id": "chart_correlation",
      "title": "增长模式与波动性分析",
      "config": {
        "title": {
          "text": "增长模式与波动性分析",
          "left": "center"
        },
        "xAxis": {
          "type": "category",
          "data": ["1月", "2月", "3月", "4月", "5月", "6月", "7月", "8月"],
          "name": "月份"
        },
        "yAxis": [
          {
            "type": "value",
            "name": "变化金额（亿美元）",
            "position": "left"
          },
          {
            "type": "value",
            "name": "累计增长（亿美元）",
            "position": "right"
          }
        ],
        "series": [
          {
            "name": "月度变化",
            "type": "bar",
            "data": [66.79, 182, 134.41, 410, 36, 321.67, -251.87, 299.19],
            "yAxisIndex": 0
          },
          {
            "name": "累计增长",
            "type": "line",
            "data": [66.79, 248.79, 383.2, 793.2, 829.2, 1150.87, 899, 1198.19],
            "yAxisIndex": 1,
            "smooth": true
          }
        ],
        "tooltip": {
          "trigger": "axis",
          "axisPointer": {
            "type": "cross"
          }
        }
      }
    }
  ]
}

